mapper.select:
SELECT reserve.id AS id, reserve.shop_id AS shopId, reserve.room_id AS roomId, room.room_number AS roomNumber, type.name AS typeName, reserve.check_in_date AS checkInDate, reserve.check_out_date AS checkOutDate, reserve.status AS status, reserve.create_time AS createTime FROM zmsk_room_reserve reserve LEFT JOIN zmsk_guest_room room ON room.id = reserve.room_id LEFT JOIN zmsk_room_type type ON type.id = room.room_type_id WHERE reserve.shop_id = #{shopId} AND reserve.status = 1
AND room.room_number = #{u.roomNumber}
ORDER BY reserve.create_time DESC
